template.value:
function Counter(options) {
  this.options = {
    el   : '.counterwrap',
    add  : '.up',
    sub  : '.down',
    value: '.counter'
  };

  for (var i in options) {
    this.options[i] = options[i];
  }

  this.result  = 0;
  this.addEl   = document.querySelector(this.options.add);
  this.subEl   = document.querySelector(this.options.sub);
  this.valueEl = document.querySelector(this.options.value);

  this.addEl.addEventListener('touchstart', function() {
    this.result++;

    this.valueEl.innerHTML = this.result;
  }.bind(this));

  this.subEl.addEventListener('touchstart', function() {
    this.result--;

    if (this.result < 0) {
      this.result = 0;
    }
    this.valueEl.innerHTML = this.result;
  }.bind(this));
}

function RiseTop(options) {
  this.options = {
    el      : '.backtotop',
    duration: undefined,
  };

  for (var i in options) {
    this.options[i] = options[i];
  }

  this.el = document.querySelector(this.options.el);

  this.rising        = false;
  this.startPosition = 0;
  this.endPosition   = 0;

  this.el.addEventListener('touchstart', this.rise.bind(this));

  // init
  if (document.body.scrollTop > 500) {
    this.el.style.display = 'block';
  } else {
    this.el.style.display = 'none';
  }

  window.addEventListener('scroll', function() {
    if (document.body.scrollTop > 500) {
      this.el.style.display = 'block';
    } else {
      this.el.style.display = 'none';
    }
  }.bind(this));
}

RiseTop.prototype.rise = function() {
  if (this.rising) {
    return;
  }

  this.rising = true;

  this.startPosition = document.body.scrollTop;
  this.endPosition   = 0;
  this.duration      = this.options.duration ? this.options.duration : ( Math.abs(this.endPosition - this.startPosition) * .8);

  requestAnimationFrame(this.animateLoop.bind(this));
};

RiseTop.prototype.easeInOutQuad = function(t, b, c, d) {
  t /= d / 2;
  if (t < 1) return c / 2 * t * t + b;
  t--;
  return -c / 2 * ( t * ( t - 2 ) - 1 ) + b;
};

RiseTop.prototype.animateLoop = function(time) {
  if (!this.startTime) {
    this.startTime = time;
  }

  var timeSoFar     = time - this.startTime;
  var easedPosition = this.easeInOutQuad(timeSoFar, this.startPosition, this.endPosition - this.startPosition, this.duration);

  window.scrollTo(0, easedPosition);

  if (timeSoFar < this.duration) {
    this.animation = requestAnimationFrame(this.animateLoop.bind(this));
  } else {
    this.animationFinished();
  }
};

RiseTop.prototype.animationFinished = function() {
  this.startTime     = null;
  this.startPosition = null;
  this.rising        = false;
};

function Timer(options) {
  this.options = {
    duration: 0,
    format  : '距定制开始还剩dd天hh时mm分ss秒',
  };

  for (var i in options) {
    this.options[i] = options[i];
  }

  this.timer    = null;
  this.duration = this.options.duration;
  this._events  = [];
}

Timer.prototype.start = function() {
  clearInterval(this.timer);

  this.timer = setInterval(function() {
    this.duration -= 1000;
    this._execEvent('change');

    if (this.duration < 0) {
      this.duration = 0;
      clearInterval(this.timer);
      this.timer = null;
    }
  }.bind(this), 1000);
};

Timer.DAY    = 24 * 60 * 60 * 1000;
Timer.HOUR   = 60 * 60 * 1000;
Timer.MINUTE = 60 * 1000;
Timer.SECOND = 1000;

Timer.prototype.toString = function() {
  var str = this.options.format;
  str     = str.replace('dd', this.getDay());
  str     = str.replace('hh', this.getHours());
  str     = str.replace('mm', this.getMinutes());
  str     = str.replace('ss', this.getSeconds());
  return str;
};

Timer.prototype.on = function(type, fn) {
  if (!this._events[type]) {
    this._events[type] = [];
  }

  this._events[type].push(fn);
};

Timer.prototype.off = function(type, fn) {
  if (!this._events[type]) {
    return;
  }

  var index = this._events[type].indexOf(fn);

  if (index > -1) {
    this._events[type].splice(index, 1);
  }
};

Timer.prototype._execEvent = function(type) {
  if (!this._events[type]) {
    return;
  }

  var i = 0,
      l = this._events[type].length;

  if (!l) {
    return;
  }

  for (; i < l; i++) {
    this._events[type][i].apply(this, [].slice.call(arguments, 1));
  }
};

Timer.prototype.getDay = function() {
  return parseInt(this.duration / Timer.DAY);
};

Timer.prototype.getHours = function() {
  return parseInt(this.duration % Timer.DAY / Timer.HOUR);
};

Timer.prototype.getMinutes = function() {
  return parseInt(this.duration % Timer.DAY % Timer.HOUR / Timer.MINUTE);
};

Timer.prototype.getSeconds = function() {
  return parseInt(this.duration % Timer.DAY % Timer.HOUR % Timer.MINUTE / Timer.SECOND);
};

;(function(window, $) {
  function CascadingSelect(options) {
    this._defaults = {
      el: '.CascadingSelect select'
    }

    this.options = {};
    $.extend(true, this.options, this._defaults, options);

    this._init();
  }

  CascadingSelect.prototype._init = function() {
    this.$selects = $(this.options.el);

    this.$selects.each(function(index, value) {
      var $value = $(value);
      if (index < this.$selects.length - 1) {
        $value.change(function() {
          var data = this.options.data;
          for (var i = 0; i <= index; i++) {
            var selectedIndex = this.$selects.eq(i).find(':selected').index();
            if (data[selectedIndex].children) {
              data = data[this.$selects.eq(i).find(':selected').index()].children;
            }
          }
          this.$selects.eq(index + 1).empty().append(this._createOption(data));
          for (var i = index + 2; i < this.$selects.length; i++) {
            this.$selects.eq(i).empty();
          }
        }.bind(this));
      }
    }.bind(this));

    // FIXME: 与上面的change是同样的逻辑,代码需要优化.
    this.$selects.eq(0).empty().append(this._createOption(this.options.data));
    var data = this.options.data;
    for (var i = 0; i <= 0; i++) {
      var selectedIndex = this.$selects.eq(i).find(':selected').index();
      if (data[selectedIndex].children) {
        data = data[this.$selects.eq(i).find(':selected').index()].children;
      }
    }
    this.$selects.eq(1).empty().append(this._createOption(data));
    for (var i = 2; i < this.$selects.length; i++) {
      this.$selects.eq(i).empty();
    }
  };

  CascadingSelect.prototype._createOption = function(array) {
    var ret = [];
    if (!$.isArray(array)) return ret;

    $.each(array, function(index, obj) {
      if (!obj.value && !obj.title) return;

      ret.push($('<option value="' + obj.value + '">' + obj.title + '</option>'));
    });
    return ret;
  };

  window.CascadingSelect = CascadingSelect;
})(window, jQuery);

function Tabs(options) {
  this.options = {};

  $.extend(this.options, options);

  this.menus = this.options.menus.selector ? this.options.menus : $(this.options.menus);
  this.tabs = this.options.tabs.selector ? this.options.tabs : $(this.options.tabs);

  this._init();
}

Tabs.prototype._init = function() {
  $.each(this.menus, function(index, value) {
    var $el = value.selector ? value : $(value);

    $el.click(function() {
      this.show(index);
    }.bind(this));

    if (this.options.canHover) {
      $el.hover(function() {
        this.show(index);
      }.bind(this));
    }

  }.bind(this));
};

Tabs.prototype._showMenu = function($el) {
  $el.addClass('active');
};

Tabs.prototype._hideMenu = function($el) {
  $el.removeClass('active');
};

Tabs.prototype._showTab = function($el) {
  $el.addClass('active');
};

Tabs.prototype._hideTab = function($el) {
  $el.removeClass('active');
};

Tabs.prototype.show = function(index) {
  this._hideMenu(this.menus);
  this._hideTab(this.tabs);
  this._showMenu(this.menus.eq(index));
  this._showTab(this.tabs.eq(index));
};

$('[role="tabs"]').each(function() {
  var $this = $(this);
  var menus = $this.attr('role-menu');
  var tabs = $this.attr('role-tab');
  var canHover = $this.attr('role-tab-hover') !== undefined;

  if (!menus || !tabs) {
    console.warn('must set data-menu and data-tab');
  }

  var tab = new Tabs({
    menus: $this.find(menus),
    tabs: $this.find(tabs),
    canHover: canHover,
  });

  tab.show(0);
});

$.fn.scrollTo = function(options) {
            var defaults = {
                toT: 0,    //滚动目标位置
                durTime: 300,  //过渡动画时间
                delay: 16,     //定时器时间
                callback: null   //回调函数
            };
            var opts = $.extend(defaults, options),
                    timer = null,
                    _this = this,
                    curTop = _this.scrollTop(),//滚动条当前的位置
                    subTop = opts.toT - curTop,    //滚动条目标位置和当前位置的差值
                    index = 0,
                    dur = Math.round(opts.durTime / opts.delay),
                    smoothScroll = function(t) {
                        index++;
                        var per = Math.round(subTop / dur);
                        if (index >= dur) {
                            _this.scrollTop(t);
                            window.clearInterval(timer);
                            if (opts.callback && typeof opts.callback == 'function') {
                                opts.callback();
                            }
                            return;
                        } else {
                            _this.scrollTop(curTop + index * per);
                        }
                    };
            timer = window.setInterval(function() {
                smoothScroll(opts.toT);
            }, opts.delay);
            return _this;
        };
